#!/bin/sh
#
# file:        test-1.sh
# description: Tests for question 1
#
# Akshdeep Rungta, Hongxiang Wang

if [ x$1 = x-v ] ; then
    verbose=t; shift
fi

cmd=./homework
disk=/tmp/$USER-disk1.img

for f in $cmd; do
  if [ ! -f $f ] ; then
      echo "Unable to access: $f"
      exit
  fi
done
./mktest $disk

echo Testing question 1

$cmd -cmdline -image $disk > /tmp/output-$$ <<EOF
ls
ls dir1
ls dir2
cd dir1
ls-l file.2
ls-l file.270
ls-l file.999
show file.2
cd ..
show nofile
blksiz 5
show file.A
blksiz 20
show file.A
blksiz 100
show file.A
blksiz 2000
show file.A
statfs
EOF
echo >> /tmp/output-$$
[ "$verbose" ] && echo wrote /tmp/test1-output-$$

diff - /tmp/output-$$ <<EOF
read/write block size: 1000
cmd> ls
dir1
file.7
file.A
cmd> ls dir1
file.0
file.2
file.270
cmd> ls dir2
error: No such file or directory
cmd> cd dir1
cmd> ls-l file.2
/dir1/file.2 -rwxrwxrwx 2012 2 Fri Jul 13 11:04:40 2012
cmd> ls-l file.270
/dir1/file.270 -rwxrwxrwx 276177 270 Fri Jul 13 11:06:20 2012
cmd> ls-l file.999
error: No such file or directory
cmd> show file.2
> cd ..
cmd> show nofile
error: No such file or directory
cmd> blksiz 5
read/write block size: 5
cmd> show file.A
> blksiz 20
read/write block size: 20
cmd> show file.A
> blksiz 100
read/write block size: 100
cmd> show file.A
> blksiz 2000
read/write block size: 2000
cmd> show file.A
> statfs
max name length: 27
block size: 1024
cmd> 
EOF

if [ $? != 0 ] ; then
    echo 'Tests may have failed - see above output for details'
else
    echo 'Tests passed - output matches exactly'
fi
[ "$verbose" ] || rm -f /tmp/output-$$
